import facebook from '../../images/facebook.svg';
import instagram from '../../images/instagram.svg';
// import twitter from '../../images/twitter.svg';
import youtube from '../../images/youtube.svg';
// import tumblr from '../../images/tumblr.svg'

import main from '../../images/main.jpg';
import work1 from '../../images/work1.JPG';
import work2 from '../../images/work2.JPG';
import work3 from '../../images/work3.JPG';

import leaf1 from '../../images/leaf1.JPG';
import leaf2 from '../../images/leaf2.JPG';
import leaf3 from '../../images/leaf3.JPG';
import leaf5 from '../../images/leaf5.JPG';
import leaf6 from '../../images/leaf4-cropped.png';

import { useState } from 'react';

function Home() {

    const alert = (text,display,icon) =>{
        setAlertText(text);
        setAlertDisplay(display);
        setAlertIcon(icon); 
        setTimeout(function(){
            setAlertDisplay("alert");  
            setDisabled(false);
        },2000);
    };
    const handleContact = () => () =>{
        setDisabled(true);

        const data = { email: contactEmail, message: contactMessage, name: contactName };
        if(contactName === ''){
            contactNameFocus.current.focus();
            alert("Name is required!!","alert show","!success");

        }
        else if(contactEmail === ''){
            contactEmailFocus.current.focus();
            alert("Email is required!!","alert show","!success");

        } 
        else if(contactMessage === ''){
            contactMessageFocus.current.focus();
            alert("Message is required!!","alert show","!success");


        }
        else{
            axios.post('/contacts', data)
            .then(res => {
                if(res.data.status === 200){
                    console.log(res.data.message);
                    alert(res.data.message,"alert show","success");
                    setContactEmail("");
                    setContactName("");
                    setContactMessage("")
                }
                else{
                    alert(res.data.message.email.message,"alert show","!success");

                }
            })
            .catch(rejected => {
                alert("There is sonething wrong. Please try again later.","alert show","!success");
            });
        }

    }

    const contactNameFocus = useRef(null);
    const contactEmailFocus = useRef(null);
    const contactMessageFocus = useRef(null);

    const [contactName,setContactName] = useState('');
    const [contactEmail,setContactEmail] = useState('');
    const [contactMessage,setContactMessage] = useState('');

    const [alertText,setAlertText] = useState('');
    const [alertDisplay,setAlertDisplay] = useState('alert');
    const [alertIcon,setAlertIcon] = useState('');
    const [disabled,setDisabled] = useState(false);





   const [active,setActive] = useState(0); 
   const changeActive = () =>{

        if(active != 3){


            setActive(active+1);
            setTimeout(function(){
                const scroll = document.querySelector("#main_imgdiv>div .active").offsetLeft;
                document.querySelector("#main_imgdiv>div").scrollLeft = scroll;
            },200)


        }
        else{
            setActive(0);
            setTimeout(function(){
                const scroll = document.querySelector("#main_imgdiv>div .active").offsetLeft;
                document.querySelector("#main_imgdiv>div").scrollLeft = scroll;
            },200)
        }
   }
  return (
    <div>

        <main id="main1">
            <div id="main_imgdiv">
                <div onMouseEnter={changeActive}>

                    <img src={work1} className={active=== 0 ? "active" : ""} alt="Tea Leafs"></img>
                    <img src={main} className={active=== 1 ? "active" : ""} alt="Tea Leafs"></img>

                    <img src={work2} className={active=== 2 ? "active" : ""} alt="Tea Leafs"></img>

                    <img src={work3} className={active=== 3 ? "active" : ""} alt="Tea Leafs"></img>
                </div>

            </div>
            <div className="main_div">
                <h1>Awesome Tea</h1>
                <p>Tatparya is a magazine focused towards inspiring the youth.It's also about bridging the gap between youth and elders.This magazine helps the youth by understanding the problems of new generation and keep them motivated.</p>		
                <div>
                    <a href="https://www.facebook.com/Tatparya-694820310973090"><img src={facebook}></img></a>
                    <a href="http://instagram.com/tatparya_"><img src={instagram}></img></a>
                    {/* <a href="https://twitter.com/Tatparya3?s=08"><img src={twitter}></img></a>				 */}
                    <a href="https://youtu.be/OXc_G_uzGYg"><img src={youtube}></img></a>
                    {/* <a href="https://www.tumblr.com/blog/tatparya"><img src={tumblr}></img></a> */}
                </div>
            </div>
        </main>
        <main id="main2">

            <div id="main_imgdiv2">
                <img src={leaf1}></img>
                <img src={leaf2}></img>
                <img src={leaf3}></img>
                <img src={leaf5}></img>
            </div>

            <div className="main_div">
                <h2>Learn how to free yourself from anxieties with these 8 easy steps</h2>
                <p>This is your blog post. To really engage your site visitors we suggest you blog about subjects that are related to your site or business. Blogging is really great for SEO, so we recommend including keywords that relate to your services, products or industry within your posts. It'll make it easier for people to find you on the web. Ready to delete this post and add your own? Go to Manage to open your Dashboard and click on the 3 dot icon ( â ‡), then click Delete Post. You can a</p>		
                <div>
                    <button>Read More</button>	
                </div>
            </div>
        </main>
        <section>

            <div className="slides">
                <h3>Career Coaching Session</h3>
                <p>Require career guidance? Book a session soon.</p>
            </div>

            <img src={leaf6}></img>

        </section>
        <main id="main3">

            <div id="main_imgdiv3">

            </div>
            <div className="main_div">

                <h2>Contact Us</h2>
                <p>We will get in touch with you asap.</p>
                <form action="" method="post">
                    <div>
                        <input type="text" placeholder="Name"></input>
                    </div>
                    <div>
                        <input type="email" name="" placeholder="Email"></input>
                    </div>
                    <div>
                        <textarea style={{minHeight: 200}} id="message" onChange={event => setContactMessage(event.target.value)} ref={contactMessageFocus} value={contactMessage}></textarea>

                    </div>
                </form>
                <button style={disabled ? style.button.disabled : style.button} onClick={handleContact()} disabled={disabled}>Send this message<FaLongArrowAltRight /></button>

            </div>	
        </main>
    </div>



  );
}

export default Home;
