import logo from '../../images/tea.png';
import { useState ,useEffect} from 'react';

function Navbar() {
    const [active,setActive] = useState("home");
    useEffect(() => {

        window.addEventListener('scroll', scrollHandler);

        return () => window.removeEventListener('scroll', scrollHandler);

    }, []);

    const scrollHandler = () => {
        const main1 = document.querySelector("#main1").scrollTop;
        const main2 = document.querySelector("#main2").scrollTop;
        const main3 = document.querySelector("#main3").scrollTop;
        const scroll = document.documentElement.scrollTop;
        console.log(scroll);
        console.log(main1);
        console.log(main2);
        console.log(main3);
        if(scroll >= main1 && scroll < main2){
            setActive("home");
        }
        else if(scroll >= main2 && scroll < main3){
            setActive("blog");
        }
        else if(scroll >= main3){
            setActive("contact");
        }

    }
    return (
        <header>
            <nav>
                <div id="logo_div">
                    <img src={logo}></img>
                </div>
                <div id="link_div"> 
                    <a href="#" className={active==="home" ? "active" : ""}>Home</a>				
                    <a href="#" className={active==="blog" ? "active" : ""}>Blog</a>
                    <a href="#" className={active==="contact" ? "active" : ""}>Contact</a>
                </div>
            </nav>
        </header>
  );
}

export default Navbar;
