import logo from '../../images/tea.png';
import { useState ,useEffect} from 'react';
import Hamburger from 'hamburger-react'

const styles = {
    active:{
        top: "100%",
    },
    notActive:{
        bottom: "100%",
    }
}
function Navbar() {
    const [isOpen, setOpen] = useState(false)
    const [nav,setNav] = useState(false);
    const [active,setActive] = useState("home");
    useEffect(() => {

        window.addEventListener('scroll', scrollHandler);

        return () => window.removeEventListener('scroll', scrollHandler);

    }, []);

    const scrollHandler = () => {
        const main1 = document.querySelector("#main1").offsetTop;
        const main2 = document.querySelector("#main2").offsetTop;
        const main3 = document.querySelector("#main3").offsetTop;
        const scroll = document.documentElement.scrollTop;
        if(scroll >= main1 && scroll < main2-94){
            setActive("home");
        }
        else if(scroll >= main2 && scroll < main3-94){
            setActive("blog");
        }
        else if(scroll >= main3){
            setActive("contact");
        }

    }
    return (
        <header>
            <nav>
                <div id="logo_div">
                    <img src={logo}></img>
                </div>

                <div id="link_div" style={nav === true ? styles.active : ""}> 
                    <a href="#main3" className={active==="contact" ? "active" : ""}>Contact</a>
                    <a href="#main2" className={active==="blog" ? "active" : ""}>Blog</a>
                    <a href="#main1" className={active==="home" ? "active" : ""}>Home</a>				

                </div>
                <Hamburger toggled={isOpen} size={36} toggle={setOpen} color="#5eb562" onToggle={toggled => {
                    if (toggled) {
                        setNav(true);
                    } else {
                        setNav(false);
                    }
                }}/>

            </nav>
            <button id="up"><i className="fas fa-angle-up"></i></button>

        </header>
  );
}

export default Navbar;
