import logo from '../../images/tea.png';
import { useState ,useEffect} from 'react';

function Navbar() {
    const [active,setActive] = useState("home");
    useEffect(() => {

        window.addEventListener('scroll', scrollHandler);

        return () => window.removeEventListener('scroll', scrollHandler);

    }, []);

    const scrollHandler = () => {
        const main1 = document.querySelector("#main1").offsetTop;
        const main2 = document.querySelector("#main2").offsetTop;
        const main3 = document.querySelector("#main3").offsetTop;
        const scroll = document.documentElement.scrollTop;
        if(scroll >= main1 && scroll < main2-94){
            setActive("home");
        }
        else if(scroll >= main2 && scroll < main3-94){
            setActive("blog");
        }
        else if(scroll >= main3){
            setActive("contact");
        }

    }
    const changeActive = (val) =>{
        setActive(val);
        const main1 = document.querySelector("#main1").offsetTop;
        const main2 = document.querySelector("#main2").offsetTop;
        const main3 = document.querySelector("#main3").offsetTop;
        if(val === "home")
        document.documentElement.scrollTop = 0;
        else if(val === "blog")
        document.documentElement.scrollTop =200;
        else
        document.documentElement.scrollTop = 0;

    }
    return (
        <header>
            <nav>
                <div id="logo_div">
                    <img src={logo}></img>
                </div>
                <div id="link_div"> 
                    <a href="#" className={active==="contact" ? "active" : ""} onClick={()=>changeActive("contact")}>Contact</a>
                    <a href="#" className={active==="blog" ? "active" : ""} onClick={()=>changeActive("blog")}>Blog</a>
                    <a href="#" className={active==="home" ? "active" : ""} onClick={()=>changeActive("home")}>Home</a>				

                </div>
            </nav>
        </header>
  );
}

export default Navbar;
